import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import './App.css';

// Import components
import Navigation from './components/Navigation';
import Dashboard from './components/Dashboard';
import TestTrading from './components/TestTrading';
import Portfolio from './components/Portfolio';
import Strategies from './components/Strategies';
import Settings from './components/Settings';
import LoadingSpinner from './components/LoadingSpinner';

// Configure axios defaults
const API_BASE_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:8001';
axios.defaults.baseURL = API_BASE_URL;

function App() {
  const [loading, setLoading] = useState(true);
  const [apiStatus, setApiStatus] = useState(null);

  useEffect(() => {
    // Check API status on startup
    checkApiStatus();
  }, []);

  const checkApiStatus = async () => {
    try {
      const response = await axios.get('/api/health');
      if (response.data.status === 'healthy') {
        setApiStatus('connected');
      }
    } catch (error) {
      console.error('API connection failed:', error);
      setApiStatus('disconnected');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <Router>
      <div className="min-h-screen bg-gray-900 text-white">
        {/* Header */}
        <header className="bg-gray-800 border-b border-gray-700 px-6 py-4">
          <div className="max-w-7xl mx-auto flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <h1 className="text-2xl font-bold text-blue-400">Hypertrader 1.5</h1>
              <div className={`flex items-center space-x-2 px-3 py-1 rounded-full text-sm ${
                apiStatus === 'connected' 
                  ? 'bg-green-900 text-green-300' 
                  : 'bg-red-900 text-red-300'
              }`}>
                <div className={`w-2 h-2 rounded-full ${
                  apiStatus === 'connected' ? 'bg-green-400' : 'bg-red-400'
                }`}></div>
                <span>{apiStatus === 'connected' ? 'Connected' : 'Disconnected'}</span>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <span className="text-gray-400">Web Trading Platform</span>
            </div>
          </div>
        </header>

        <div className="flex h-screen pt-16">
          {/* Navigation Sidebar */}
          <Navigation />

          {/* Main Content */}
          <main className="flex-1 overflow-y-auto bg-gray-900">
            <Routes>
              <Route path="/" element={<Navigate to="/dashboard" replace />} />
              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/trading" element={<Trading />} />
              <Route path="/portfolio" element={<Portfolio />} />
              <Route path="/strategies" element={<Strategies />} />
              <Route path="/settings" element={<Settings />} />
            </Routes>
          </main>
        </div>
      </div>
    </Router>
  );
}

export default App;
